import React, {Component} from 'react';
import api from '../../services/api';

export default class Main extends Component{

    state = {
        products:[]
    }

    //ciclo de vida, assim que exibir na tela 
    componentDidMount(){
        this.loadProducts();

    }
    loadProducts = async() =>{
        const response = await api.get('/products');
        this.setState({ produts: response.data.docs});

    };
    render(){
        return <h1>TEte </h1>
    }
}
